@implementation SignsData

+ (NSArray *) getSignsTypes
{
    NSMutableArray *signTypes = [@[] mutableCopy];
    
    [signTypes addObject:@{TYPE_NUMBER: @"1", TYPE_CODE:@"Warning", TYPE_NAME: @"Предупреждающие знаки", TYPE_IMAGE_NAME: @"Warning.png"}];
    [signTypes addObject:@{TYPE_NUMBER: @"2", TYPE_CODE:@"Priority", TYPE_NAME: @"Знаки приоритета", TYPE_IMAGE_NAME: @"Priority.png"}];
    [signTypes addObject:@{TYPE_NUMBER: @"3", TYPE_CODE:@"Denial", TYPE_NAME: @"Запрещающие знаки", TYPE_IMAGE_NAME: @"Denial.png"}];
    [signTypes addObject:@{TYPE_NUMBER: @"4", TYPE_CODE:@"Dictating", TYPE_NAME: @"Предписывающие знаки", TYPE_IMAGE_NAME: @"Dictating.png"}];
    [signTypes addObject:@{TYPE_NUMBER: @"5", TYPE_CODE:@"Information", TYPE_NAME: @"Информационно-указательные знаки", TYPE_IMAGE_NAME: @"Information.png"}];
    [signTypes addObject:@{TYPE_NUMBER: @"6", TYPE_CODE:@"Service", TYPE_NAME: @"Знаки сервиса", TYPE_IMAGE_NAME: @"Service.png"}];
    [signTypes addObject:@{TYPE_NUMBER: @"7", TYPE_CODE:@"Addition", TYPE_NAME: @"Знаки дополнительной информации (таблички)", TYPE_IMAGE_NAME: @"Addition.png"}];

    return [signTypes copy];
}

+ (NSDictionary *) getGroupByCode:(NSString *)groupCode
{
    NSArray *allTheGroups = [self getSignsTypes];
    for (NSDictionary *currentGroup in allTheGroups)
    {
        if ([currentGroup[TYPE_CODE] isEqualToString:groupCode])
        {
            return currentGroup;
        }
    }
    
    return nil;
}

