    [allTheSigns addObject:@{SIGN_NUMBER: @"4.4", SIGN_TYPE:@"Dictating", SIGN_TITLE:@"«Движение легковых автомобилей»", SIGN_DESCRIPTION: @"Разрешается движение легковых автомобилей, автобусов, мотоциклов, маршрутных транспортных средств и грузовых автомобилей, разрешенная максимальная масса которых не превышает 3,5 т.\nЗнак разрешает движение и других транспортных средств, которые обслуживают предприятия, находящиеся в обозначенной зоне, а также обслуживают граждан или принадлежат гражданам, проживающим или работающим в обозначенной зоне. В этих случаях транспортные средства должны въезжать в обозначенную зону и выезжать из нее на ближайшем к месту назначения перекрестке.", SIGN_IMAGE_NAME: @"4_4.png"}];
