    [allTheSigns addObject:@{SIGN_NUMBER: @"5.8.7", SIGN_TYPE:@"Information", SIGN_TITLE:@"«Направление движения по полосам»", SIGN_DESCRIPTION: @"Если на знаке 5.8.7 изображен знак, запрещающий движение каким-либо транспортным средствам, то движение этих транспортных средств по соответствующей полосе запрещается.\nЗнаки 5.8.7 и 5.8.8 с соответствующим числом стрелок могут применяться на дорогах с четырьмя полосами и более.\nС помощью знаков 5.8.7 и 5.8.8 со сменным изображением может быть организовано реверсивное движение.\n", SIGN_IMAGE_NAME: @"5_8_7_1.png"}];
