+ (NSArray *) getSignsWithType:(NSString *)signType
{
    NSMutableArray *signs = [@[] mutableCopy];
    
    for (NSDictionary *currentSign in [self getAllTheSigns])
    {
        if ([currentSign[SIGN_TYPE] isEqualToString:signType])
             {
                 [signs addObject:currentSign];
             }
    }
    
    return [signs copy];
}

+ (NSDictionary *) getSignWithNumber:(NSString *)signNumber
{
    NSArray *allTheSigns = [self getAllTheSigns];
    for (NSDictionary *currentSign in allTheSigns)
    {
        if ([currentSign[SIGN_NUMBER] isEqualToString:signNumber])
        {
            return currentSign;
        }
    }
    
    return nil;
}

+ (NSArray *) getAllTheSignsInfo
{
    NSMutableArray *signsInfos = [@[] mutableCopy];
    
    [signsInfos addObject:@{TYPE_CODE:@"Warning",  GROUP_INFO_TEXT: @"Предупреждающие знаки 1.1, 1.2, 1.5-1.30 вне населенных пунктов устанавливаются на расстоянии 150 — 300 м, а в населенных пунктах и жилых зонах — на расстоянии 50 — 100 м до начала опасного участка. При необходимости знаки могут устанавливаться и на ином расстоянии, которое в этом случае указывается на табличке 7.1.1.\n\nЗнаки 1.13 и 1.14 могут устанавливаться без таблички 7.1.1 непосредственно перед началом спуска или подъема, если спуски и подъемы следуют друг за другом.\n\nЗнак 1.23 при проведении краткосрочных работ на проезжей части может быть установлен (без таблички 7.1.1) на расстоянии 10-15 м до места проведения работ.\n\nВне населенных пунктов знаки 1.1, 1.2, 1.9, 1.10, 1.21 и 1.23 повторяются. Второй знак устанавливается на расстоянии не менее 50 м до начала опасного участка.\n\nЗнаки 1.4.1 — 1.4.3 устанавливаются с правой стороны дороги, а знаки 1.4.4 — 1.4.6 — с левой. Знаки 1.4.1 и 1.4.4 — под первым по ходу движения знаком 1.1 или 1.2, знаки 1.4.3 и 1.4.6 — под вторым знаком 1.1 или 1.2, а знаки 1.4.2 и 1.4.5 — самостоятельно, на равном расстоянии между первым и вторым знаком 1.1 или 1.2.\n\nЗнаки 1.3.1 и 1.3.2 устанавливаются непосредственно перед железнодорожным переездом."}];
    [signsInfos addObject:@{TYPE_CODE:@"Denial",  GROUP_INFO_TEXT: @"Знаки 3.2-3.9, 3.32 и 3.33 запрещают движение соответствующих видов транспортных средств в обоих направлениях.\n\nДействие знаков не распространяется:\n\n3.1-3.3, 3.18.1, 3.18.2, 3.19, 3.27 — на маршрутные транспортные средства;\n3.2-3.8 — на транспортные средства, которые обслуживают предприятия, находящиеся в обозначенной зоне, а также обслуживают граждан или принадлежат гражданам, проживающим или работающим в обозначенной зоне. В этих случаях транспортные средства должны въезжать в обозначенную зону и выезжать из нее на ближайшем к месту назначения перекрестке;\n3.28-3.30 — на такси с включенным таксометром;\n3.2, 3.3, 3.28-3.30 — на транспортные средства, управляемые инвалидами I и II групп или перевозящие таких инвалидов.\nДействие знаков 3.18.1, 3.18.2 распространяется на пересечение проезжих частей, перед которым установлен знак.\n\nЗона действия знаков 3.16, 3.20, 3.22, 3.24, 3.26-3.30 распространяется от места установки знака до ближайшего перекрестка за ним, а в населенных пунктах при отсутствии перекрестка — до конца населенного пункта. Действие знаков не прерывается в местах выезда с прилегающих к дороге территорий.\n\nДействие знака 3.24, установленного перед населенным пунктом, обозначенным знаком 5.22, распространяется до этого знака.\n\nЗона действия знаков может быть уменьшена:\n\nдля знаков 3.16 и 3.26 применением таблички 7.2.1;\nдля знаков 3.20, 3.22, 3.24 установкой в конце зоны их действия соответственно знаков 3.21, 3.23, 3.25 или применением таблички 7.2.1. Зона действия знака 3.24 может быть уменьшена установкой знака 3.24 с другим значением максимальной скорости движения;\nдля знаков 3.27-3.30 установкой в конце зоны их действия повторных знаков 3.27-3.30 с табличкой 7.2.3 или применением таблички 7.2.2. Знак 3.27 может быть применен совместно с разметкой 1.4, а знак 3.28 — с разметкой 1.10, при этом зона действия знаков определяется протяженностью линии разметки.\nДействие знаков 3.10, 3.27-3.30 распространяется только на ту сторону дороги, на которой они установлены."}];
    [signsInfos addObject:@{TYPE_CODE:@"Information",  GROUP_INFO_TEXT: @"На знаках 5.20.1, 5.20.2, 5.21.1 и 5.21.2, установленных вне населенного пункта, зеленый или синий фон означает, что движение к указанному населенному пункту или объекту будет осуществляться соответственно по автомагистрали или другой дороге.\n\nНа знаках 5.20.1, 5.20.2, 5.21.1 и 5.21.2, установленных в населенном пункте, зеленый или синий фон означает, что движение к указанному населенному пункту или объекту после выезда из данного населенного пункта будет осуществляться соответственно по автомагистрали или другой дороге; белый фон означает, что указанный объект находится в данном населенном пункте."}];
    [signsInfos addObject:@{TYPE_CODE:@"Addition",  GROUP_INFO_TEXT: @"Таблички размещаются непосредственно под знаком, с которым они применены. Таблички 7.2.2-7.2.4, 7.13 при расположении знаков над проезжей частью, обочиной или тротуаром размещаются сбоку от знака."}];
    
    return [signsInfos copy];
}

+ (NSString *) getInfoForSignGroup:(NSString *)groupCode
{
    NSArray *allTheInfos = [self getAllTheSignsInfo];
    for (NSDictionary *currentInfo in allTheInfos)
    {
        if ([currentInfo[TYPE_CODE] isEqualToString:groupCode])
        {
            return currentInfo[GROUP_INFO_TEXT];
        }
    }
    
    return nil;
}

