    [allTheSigns addObject:@{SIGN_NUMBER: @"3.2", SIGN_TYPE:@"Denial", SIGN_DESCRIPTION: @"«Движение запрещено».\nЗапрещается движение всех транспортных средств.", SIGN_IMAGE_NAME: @"3_2.png"}];
