    [allTheSigns addObject:@{SIGN_NUMBER: @"3.4", SIGN_TYPE:@"Denial", SIGN_DESCRIPTION: @"«Движение грузовых автомобилей запрещено».\nЗапрещается движение грузовых автомобилей и составов транспортных средств с разрешенной максимальной массой более 3,5 т (если на знаке не указана масса) или с разрешенной максимальной массой более указанной на знаке, а также тракторов и самоходных машин.\nЗнак 3.4 не запрещает движение грузовых автомобилей с наклонной белой полосой на бортах или предназначенных для перевозки людей.", SIGN_IMAGE_NAME: @"3_4.png"}];
