    [allTheSigns addObject:@{SIGN_NUMBER: @"3.22", SIGN_TYPE:@"Denial", SIGN_DESCRIPTION: @"«Обгон грузовым автомобилям запрещен».\nЗапрещается грузовым автомобилям с разрешенной максимальной массой более 3,5 т обгон всех транспортных средств, кроме одиночных, движущихся со скоростью менее 30 км/ч. Тракторам запрещается обгон всех транспортных средств, кроме гужевых повозок и велосипедов.", SIGN_IMAGE_NAME: @"3_22.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"3.23", SIGN_TYPE:@"Denial", SIGN_DESCRIPTION: @"«Конец зоны запрещения обгона грузовым автомобилям».", SIGN_IMAGE_NAME: @"3_23.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"3.24", SIGN_TYPE:@"Denial", SIGN_DESCRIPTION: @"«Ограничение максимальной скорости».\nЗапрещается движение со скоростью (км/ч), превышающей указанную на знаке.", SIGN_IMAGE_NAME: @"3_24.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"3.25", SIGN_TYPE:@"Denial", SIGN_DESCRIPTION: @"«Конец зоны ограничения максимальной скорости».", SIGN_IMAGE_NAME: @"3_25.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"3.26", SIGN_TYPE:@"Denial", SIGN_DESCRIPTION: @"«Подача звукового сигнала запрещена».\nЗапрещается пользоваться звуковыми сигналами, кроме тех случаев, когда сигнал подается для предотвращения дорожно-транспортного происшествия.", SIGN_IMAGE_NAME: @"3_26.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"3.27", SIGN_TYPE:@"Denial", SIGN_DESCRIPTION: @"«Остановка запрещена».\nЗапрещаются остановка и стоянка транспортных средств.", SIGN_IMAGE_NAME: @"3_27.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"3.28", SIGN_TYPE:@"Denial", SIGN_DESCRIPTION: @"«Стоянка запрещена».\nЗапрещается стоянка транспортных средств.", SIGN_IMAGE_NAME: @"3_28.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"3.29", SIGN_TYPE:@"Denial", SIGN_DESCRIPTION: @"«Стоянка запрещена по нечетным числам месяца».\nПри одновременном применении знаков 3.29 и 3.30 время перестановки транспортных средств с одной стороны на другую — с 19 до 21 часа\n", SIGN_IMAGE_NAME: @"3_29.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"3.30", SIGN_TYPE:@"Denial", SIGN_DESCRIPTION: @"«Стоянка запрещена по четным числам месяца».\nПри одновременном применении знаков 3.29 и 3.30 время перестановки транспортных средств с одной стороны на другую — с 19 до 21 часа\n", SIGN_IMAGE_NAME: @"3_30.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"3.31", SIGN_TYPE:@"Denial", SIGN_DESCRIPTION: @"«Конец зоны всех ограничений».\nОбозначение конца зоны действия одновременно несколькими знаками из следующих: 3.16, 3.20, 3.22, 3.24, 3.26-3.30.", SIGN_IMAGE_NAME: @"3_31.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"3.32", SIGN_TYPE:@"Denial", SIGN_DESCRIPTION: @"«Движение транспортных средств с опасными грузами запрещено».\nЗапрещается движение транспортных средств, оборудованных опознавательными знаками «Опасный груз».", SIGN_IMAGE_NAME: @"3_32.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"3.33", SIGN_TYPE:@"Denial", SIGN_DESCRIPTION: @"«Движение транспортных средств со взрывчатыми и легковоспламеняющимися грузами запрещено».\nЗапрещается движение транспортных средств, оборудованных опознавательными знаками «Опасный груз» и предназначенных для перевозки опасных грузов классов 1,2.2-2.4, 3.1, 3.2, 5.2 по ГОСТу 19433-88.", SIGN_IMAGE_NAME: @"3_33.png"}];
