    [allTheSigns addObject:@{SIGN_NUMBER: @"4.2.1", SIGN_TYPE:@"Dictating", SIGN_DESCRIPTION: @"«Объезд препятствия справа».\nОбъезд разрешается только со стороны, указанной стрелкой.", SIGN_IMAGE_NAME: @"4_2_1.png"}];
