    [allTheSigns addObject:@{SIGN_NUMBER: @"4.3", SIGN_TYPE:@"Dictating", SIGN_DESCRIPTION: @"«Круговое движение».\nРазрешается движение в указанном стрелками направлении.", SIGN_IMAGE_NAME: @"4_3.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"4.4", SIGN_TYPE:@"Dictating", SIGN_DESCRIPTION: @"«Движение легковых автомобилей».\nРазрешается движение легковых автомобилей, автобусов, мотоциклов, маршрутных транспортных средств и грузовых автомобилей, разрешенная максимальная масса которых не превышает 3,5 т.\nЗнак разрешает движение и других транспортных средств, которые обслуживают предприятия, находящиеся в обозначенной зоне, а также обслуживают граждан или принадлежат гражданам, проживающим или работающим в обозначенной зоне. В этих случаях транспортные средства должны въезжать в обозначенную зону и выезжать из нее на ближайшем к месту назначения перекрестке.", SIGN_IMAGE_NAME: @"4_4.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"4.5", SIGN_TYPE:@"Dictating", SIGN_DESCRIPTION: @"«Велосипедная дорожка».\nРазрешается движение только на велосипедах и мопедах. По велосипедной дорожке могут двигаться также пешеходы (при отсутствии тротуара, пешеходной дорожки или обочины).", SIGN_IMAGE_NAME: @"4_5.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"4.6", SIGN_TYPE:@"Dictating", SIGN_DESCRIPTION: @"«Пешеходная дорожка».\nРазрешается движение только пешеходам.", SIGN_IMAGE_NAME: @"4_6.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"4.7", SIGN_TYPE:@"Dictating", SIGN_DESCRIPTION: @"«Ограничение минимальной скорости».\nРазрешается движение только с указанной или большей скоростью (км/ч).", SIGN_IMAGE_NAME: @"4_7.png"}];
