    [allTheSigns addObject:@{SIGN_NUMBER: @"4.9.1", SIGN_TYPE:@"Dictating", SIGN_DESCRIPTION: @"«Направление движения транспортных средств с опасными грузами».\nДвижение транспортных средств, оборудованных опознавательными знаками «Опасный груз», разрешается только направо.", SIGN_IMAGE_NAME: @"4_9_1.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"4.9.2", SIGN_TYPE:@"Dictating", SIGN_DESCRIPTION: @"«Направление движения транспортных средств с опасными грузами».\nДвижение транспортных средств, оборудованных опознавательными знаками «Опасный груз», разрешается только налево.", SIGN_IMAGE_NAME: @"4_9_2.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"4.9.3", SIGN_TYPE:@"Dictating", SIGN_DESCRIPTION: @"«Направление движения транспортных средств с опасными грузами».\nДвижение транспортных средств, оборудованных опознавательными знаками «Опасный груз», разрешается только прямо.", SIGN_IMAGE_NAME: @"4_9_3.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"5.1", SIGN_TYPE:@"Information", SIGN_DESCRIPTION: @"«Автомагистраль».\nДорога, на которой действуют требования Правил, устанавливающие порядок движения по автомагистралям.", SIGN_IMAGE_NAME: @"5_1.png"}];
