    [allTheSigns addObject:@{SIGN_NUMBER: @"5.5", SIGN_TYPE:@"Information", SIGN_DESCRIPTION: @"«Дорога с односторонним движением».\nДорога или проезжая часть, по которой движение транспортных средств по всей ширине осуществляется в одном направлении.", SIGN_IMAGE_NAME: @"5_5.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"5.6", SIGN_TYPE:@"Information", SIGN_DESCRIPTION: @"«Конец дороги с односторонним движением».", SIGN_IMAGE_NAME: @"5_6.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"5.7.1", SIGN_TYPE:@"Information", SIGN_DESCRIPTION: @"«Выезд на дорогу с односторонним движением».\nВыезд на дорогу или проезжую часть с односторонним движением.\n", SIGN_IMAGE_NAME: @"5_7_1.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"5.7.2", SIGN_TYPE:@"Information", SIGN_DESCRIPTION: @"«Выезд на дорогу с односторонним движением».\nВыезд на дорогу или проезжую часть с односторонним движением.\n", SIGN_IMAGE_NAME: @"5_7_2.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"5.8.1", SIGN_TYPE:@"Information", SIGN_DESCRIPTION: @"«Направления движения по полосам».\nЧисло полос и разрешенные направления движения на каждой из них.", SIGN_IMAGE_NAME: @"5_8_1.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"5.8.2", SIGN_TYPE:@"Information", SIGN_DESCRIPTION: @"«Направления движения по полосе».\nРазрешенные направления движения по полосе.\nЗнаки 5.8.1 и 5.8.2, разрешающие поворот налево из крайней левой полосы, разрешают и разворот с этой полосы.\nДействие знаков 5.8.1 и 5.8.2, установленных перед перекрестком, распространяется на весь перекресток, если другие знаки 5.8.1 и 5.8.2, установленные на нем, не дают иных указаний.", SIGN_IMAGE_NAME: @"5_8_2.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"5.8.3", SIGN_TYPE:@"Information", SIGN_DESCRIPTION: @"«Начало полосы».\nНачало дополнительной полосы на подъеме или полосы торможения.\nЕсли на знаке, установленном перед дополнительной полосой, изображен знак 4.7 «Ограничение минимальной скорости», то водитель транспортного средства, который не может продолжать движение по основной полосе с указанной или большей скоростью, должен перестроиться на дополнительную полосу", SIGN_IMAGE_NAME: @"5_8_3.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"5.8.4", SIGN_TYPE:@"Information", SIGN_DESCRIPTION: @"«Начало полосы».\nНачало участка средней полосы трехполосной дороги, предназначенного для движения в данном направлении.", SIGN_IMAGE_NAME: @"5_8_4.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"5.8.5", SIGN_TYPE:@"Information", SIGN_DESCRIPTION: @"«Конец полосы».\nКонец дополнительной полосы на подъеме или полосы разгона.", SIGN_IMAGE_NAME: @"5_8_5.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"5.8.6", SIGN_TYPE:@"Information", SIGN_DESCRIPTION: @"«Конец полосы».\nКонец участка средней полосы на трехполосной дороге, предназначенного для движения в данном направлении.", SIGN_IMAGE_NAME: @"5_8_6.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"5.8.7", SIGN_TYPE:@"Information", SIGN_DESCRIPTION: @"«Направление движения по полосам».\nЕсли на знаке 5.8.7 изображен знак, запрещающий движение каким-либо транспортным средствам, то движение этих транспортных средств по соответствующей полосе запрещается.\nЗнаки 5.8.7 и 5.8.8 с соответствующим числом стрелок могут применяться на дорогах с четырьмя полосами и более.\nС помощью знаков 5.8.7 и 5.8.8 со сменным изображением может быть организовано реверсивное движение.\n", SIGN_IMAGE_NAME: @"5_8_7.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"5.8.8", SIGN_TYPE:@"Information", SIGN_DESCRIPTION: @"«Направление движения по полосам».\nЕсли на знаке 5.8.7 изображен знак, запрещающий движение каким-либо транспортным средствам, то движение этих транспортных средств по соответствующей полосе запрещается.\nЗнаки 5.8.7 и 5.8.8 с соответствующим числом стрелок могут применяться на дорогах с четырьмя полосами и более.\nС помощью знаков 5.8.7 и 5.8.8 со сменным изображением может быть организовано реверсивное движение.\n", SIGN_IMAGE_NAME: @"5_8_8.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"5.9", SIGN_TYPE:@"Information", SIGN_DESCRIPTION: @"«Полоса для маршрутных транспортных средств».\nПолоса, предназначенная для движения только маршрутных транспортных средств, движущихся попутно общему потоку транспортных средств.\nДействие знака распространяется на полосу, над которой он расположен. Действие знака, установленного справа от дороги, распространяется на правую полосу.", SIGN_IMAGE_NAME: @"5_9.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"5.10.1", SIGN_TYPE:@"Information", SIGN_DESCRIPTION: @"«Дорога с полосой для маршрутных транспортных средств».\nДорога, по которой движение маршрутных транспортных средств осуществляется по специально выделенной полосе навстречу общему потоку транспортных средств.", SIGN_IMAGE_NAME: @"5_10_1.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"5.10.2", SIGN_TYPE:@"Information", SIGN_DESCRIPTION: @"«Выезд на дорогу с полосой для маршрутных транспортных средств».\n", SIGN_IMAGE_NAME: @"5_10_2.png"}];
    [allTheSigns addObject:@{SIGN_NUMBER: @"5.10.3", SIGN_TYPE:@"Information", SIGN_DESCRIPTION: @"«Выезд на дорогу с полосой для маршрутных транспортных средств».\n", SIGN_IMAGE_NAME: @"5_10_3.png"}];
