    [allTheSigns addObject:@{SIGN_NUMBER: @"5.16.1", SIGN_TYPE:@"Information", SIGN_DESCRIPTION: @"«Пешеходный переход».\nПри отсутствии на переходе разметки 1.14.1, 1.14.2 или1.14.3 знак 5.16.2 устанавливается справа от дороги на ближней границе перехода, а знак 5.16.1 — слева от дороги на дальней границе перехода.\n", SIGN_IMAGE_NAME: @"5_16_1.png"}];
