+ (NSArray *) getWarningSigns
{
    NSMutableArray *warningSigns = [@[] mutableCopy];
    
    for (NSDictionary *currentSign in [self getAllTheSigns])
    {
        if ([currentSign[SIGN_TYPE] isEqualToString:@"Warning"])
             {
                 [warningSigns addObject:currentSign];
             }
    }
    
    return [warningSigns copy];
}

+ (NSDictionary *) getSignWithNumber:(NSString *)signNumber
{
    NSArray *allTheSigns = [self getAllTheSigns];
    for (NSDictionary *currentSign in allTheSigns)
    {
        if ([currentSign[SIGN_NUMBER] isEqualToString:signNumber])
        {
            return currentSign;
        }
    }
    
    return nil;
}

